package cn.edugate.esb.comparator;

import java.util.Comparator;

import cn.edugate.esb.entity.Campus;


public class CompusToSortComparator implements Comparator<Campus>{

	@Override
	public int compare(Campus o1, Campus o2) {
		// TODO Auto-generated method stub
		  int s1=o1.getCam_id();  
		  int s2=o2.getCam_id();   
		  if(s1>s2) {  
		    return 1;  
		  } else {  
		    return -1;  
		  } 
	}

}
